package Lecture5;

public class IfElseIfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 15;
		int b = 150;
		int c= 10;
		
		if(a>=b&&a>=c) {
			System.out.println("a is largest");
		}else if(b>=a&&b>=c) {
			System.out.println(" b is largest");
		}else {
			System.out.println("c is largest");
		}
		

	}

}
