package Lecture6;

public class BitwiseOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      System.out.println(2&3);
      System.out.println(2|3);
      System.out.println(2^3);
       
      
      int num=8;
      num=num>>2;
      System.out.println(num);
      
      
      int num2=8;
      num2=num2<<2;
      System.out.println(num2);
	}

}
